import React,{useState} from "react";

function Converter() {
    // const [inpChoice, setInpChoice] = useState('decimal');
    // const [outChoice, setOutChoice] = useState('binary');

    return (
        <div id="converter" className="container">
            <div className="card">
                <h3>Input</h3>
                <input type="text" />
                <div className="radbtns">
                    <input type="radio" value="decimal" name="inp" checked> decimal</input>
                    <input type="radio" value="binary" name="inp" > binary</input>
                    <input type="radio" value="octal" name="inp" > octal</input>
                    <input type="radio" value="hexadecimal" name="inp" > hexadecimal</input>
                </div>
            </div>
            <div className="card">
                <h3>Output</h3>
                <input type="text" />
                <div className="radbtns">
                    <input type="radio" value="decimal" name="out" /> decimal
                    <input type="radio" value="binary" name="out" checked/> binary
                    <input type="radio" value="octal" name="out" /> octal
                    <input type="radio" value="hexadecimal" name="out" /> hexadecimal
                </div>
            </div>
        </div>
    )
}

export default Converter;
