import React, { useState } from "react";

const map1 = new Map([
    ["decimal", 10],
    ["binary", 2],
    ["octal", 8],
    ["hexadecimal", 16],
    ["base-32", 32]
])
const mapHexadecimalValues = new Map([
    [10,"A"],
    [11,"B"],
    [12,"C"],
    [13,"D"],
    [14,"E"],
    [15,"F"],
])



const convertNumber = (num, baseOg, baseConv) => {

    console.log(typeof(baseOg) + " " + baseConv); // map and conevrsion to type working fine

    const toDecimal = (num) => {
        let n = num;

        if (baseOg !== 10) {
            let a=0, c = 0, sum = 0;
            while (n > 0) {
                a = n % 10;
                sum += Math.pow(a, Math.pow(10, c))
                c++;
                n = n / 10;
            }
            console.log(sum)
            return sum;
        } else {
            return num;
        }
    }
    const numDec = toDecimal(num);

    const toOBase = () =>{
        let n = num;

        if (baseConv !== 10) {
            let a, sum = '';
            while (n > 0) {
                a = n % baseConv;
                if(baseConv === 16 && a>9){
                    a = mapHexadecimalValues.get(a);
                }
                sum += a;
                n = n / baseConv;
            }
            return sum;
        } else {
            return n;
        }
    }

    const finalNum = toOBase(numDec);
    return finalNum;
}


function Converter() {

    const [number, setNumber] = useState(0);
    const [outputNumber, setOutputNumber] = useState(0);

    const updateNumber = (event) =>{
        setNumber(event.target.value)

        const a = map1.get(baseOg);
        const b = map1.get(baseConv);
        setOutputNumber(convertNumber(number , a, b))
    }


    //radio button logic to decide the base to and from to convert to 
    const [inpChoice, setInpChoice] = useState('decimal');
    const [outChoice, setOutChoice] = useState('binary');
    const updateInpChoice = (event) => {
        setInpChoice(event.target.value)
        console.log(inpChoice)
    }
    const updateOutChoice = (event) => {
        setOutChoice(event.target.value)
        console.log(outChoice)
    }



    return (
        <div id="converter" className="container">
            <div className="card">
                <div className="card-head">
                    <h3>Input</h3>
                </div>
                <input type="text" onChange={updateNumber} />
                <div className="radbtns">
                    <input onClick={updateInpChoice} type="radio" value="decimal" name="inp" checked /> decimal
                    <input onClick={updateInpChoice} type="radio" value="binary" name="inp" /> binary
                    <input onClick={updateInpChoice} type="radio" value="octal" name="inp" /> octal
                    <input onClick={updateInpChoice} type="radio" value="hexadecimal" name="inp" /> hexadecimal
                </div>
            </div>
            <div className="card">
                <div className="card-head">
                    <h3>Output</h3>
                </div>
                <p type="number">{outputNumber}</p>
                <div className="radbtns">
                    <input onClick={updateOutChoice} type="radio" value="decimal" name="out" /> decimal
                    <input onClick={updateOutChoice} type="radio" value="binary" name="out" /> binary
                    <input onClick={updateOutChoice} type="radio" value="octal" name="out" /> octal
                    <input onClick={updateOutChoice} type="radio" value="hexadecimal" name="out" /> hexadecimal
                </div>
            </div>
        </div>
    )
}

export default Converter;
