import React,{useState} from "react";

function Converter() {

    const [number, setNumber] = useState(0);
    const [inpChoice, setInpChoice] = useState('decimal');
    const [outChoice, setOutChoice] = useState('binary');


    const updateInpChoice = (event) =>{
        setInpChoice(event.target.value)
        console.log(inpChoice)
    }
    const updateOutChoice = (event) =>{
        setOutChoice(event.target.value)
        console.log(outChoice)
    }
    const updateNumber = (event) =>{
        setNumber(event.target.value)

    }



    return (
        <div id="converter" className="container">
            <div className="card">
                <h3>Input</h3>
                <input type="number" onChange={updateNumber} />
                <div className="radbtns">
                    <input onClick={updateInpChoice} type="radio" value="decimal" name="inp" /> decimal
                    <input onClick={updateInpChoice} type="radio" value="binary" name="inp" /> binary
                    <input onClick={updateInpChoice} type="radio" value="octal" name="inp" /> octal
                    <input onClick={updateInpChoice} type="radio" value="hexadecimal" name="inp" /> hexadecimal
                </div>
            </div>
            <div className="card">
                <h3>Output</h3>
                <input type="number">{outputNumber}</input>
                <div className="radbtns">
                    <input onClick={updateOutChoice} type="radio" value="decimal" name="out" /> decimal
                    <input onClick={updateOutChoice} type="radio" value="binary" name="out" /> binary
                    <input onClick={updateOutChoice} type="radio" value="octal" name="out" /> octal
                    <input onClick={updateOutChoice} type="radio" value="hexadecimal" name="out" /> hexadecimal
                </div>
            </div>
        </div>
    )
}

export default Converter;
