import React, { useState , useRef} from "react";

const map1 = new Map([
    ["decimal", 10],
    ["binary", 2],
    ["octal", 8],
    ["hexadecimal", 16],
    ["base-32", 32]
])

const convertNumber = (num, baseOg, baseConv)=>{
    return parseInt(num, baseOg).toString(baseConv);
}

function Converter() {

    const inputRef = useRef(null);

    const onClick = () =>{
        inputRef.current.focus() 
    };
    // const updateOutputNumber = (num) =>{
    //     setOutputNumber(num)
    // }

    //radio button logic to decide the base to and from to convert to 
    const [inpChoice, setInpChoice] = useState('decimal');
    const [outChoice, setOutChoice] = useState('binary');
    const updateInpChoice = (event) => {
        setInpChoice(event.target.value)
        console.log(inpChoice)
    }
    const updateOutChoice = (event) => {
        setOutChoice(event.target.value)
        console.log(outChoice)
    }

    const [number, setNumber] = useState(0);
    const [outputNumber, setOutputNumber] = useState(0);

    const updateNumber = (event) =>{
        setNumber(event.target.value);
        // updateOutputNumber(number);
        updateOutputNumber(event.target.value) //passing number should be ideal but last digit entered wasn't processing
    }
    const updateOutputNumber = (num)=>{
        const a = map1.get(inpChoice);
        const b = map1.get(outChoice);

        setOutputNumber(convertNumber(num , a, b))
    }

    return (
        <div id="converter" className="container">
            <div className="card">
                <div className="card-head">
                    <h3>Input</h3>
                </div>
                <input type="text" onChange={updateNumber} ref={inputRef} />
                <div className="radbtns">
                    <input onChange={updateInpChoice} type="radio" value="decimal" name="inp" defaultChecked /> decimal
                    <input onChange={updateInpChoice} type="radio" value="binary" name="inp" /> binary
                    <input onChange={updateInpChoice} type="radio" value="octal" name="inp" /> octal
                    <input onChange={updateInpChoice} type="radio" value="hexadecimal" name="inp" /> hexadecimal
                </div>
            </div>
            <div className="card">
                <div className="card-head">
                    <h3>Output</h3>
                </div>
                {/* <p type="number">num: {number}</p> */}
                <p type="number"><span>out: {outputNumber}</span></p>
                <div className="radbtns">
                    <input onChange={updateOutChoice} type="radio" value="decimal" name="out" /> decimal
                    <input onChange={updateOutChoice} type="radio" value="binary" name="out" defaultChecked/> binary
                    <input onChange={updateOutChoice} type="radio" value="octal" name="out" /> octal
                    <input onChange={updateOutChoice} type="radio" value="hexadecimal" name="out" /> hexadecimal
                </div>
            </div>
        </div>
    )
}

export default Converter;
